query.pigql:
q = load "WebsitePerformanceUserType";
q = group q by all;
q = foreach q generate sum('Sessions') as 'totalrows';

q = load "WebsitePerformance";
q = group q by all;
q = foreach q generate (case when len("{{cell(Date_Year_1.selection,0,"Date_Year").asObject()}}")>0 then count() else 0 end) as 'count';

q = load "WebsitePerformancePageViews";q=filter q by {{column(Date_Year_1.selection,["Date_Year"]).asEquality("Date1_Year")}};q = filter q by {{column(Date_Month_1.selection,["Date_Month"]).asEquality("Date1_Month")}};q = filter q by {{column(Week_1.selection,["Week_New"]).asEquality("Week_New")}};q = filter q by  {{column(Country_1.selection,["Country"]).asEquality("Country")}};q = filter q by  {{column(Country_1.selection,["Country"]).asEquality("Country")}};
q = group q by 'Page1';
q = foreach q generate 'Page1' as 'Page1', sum('Pageviews1') as 'Pageviews2';
q = order q by 'Pageviews2' desc;
q = limit q 3;
