q = load "CustomerProfitability1";
q = group q by (Country2,Channel2,Customer_Name2,'PRODUCT_LINE_NAME2', Week_New, 'SALES_ORDER');
q = foreach q generate Country2,Channel2,Customer_Name2,'PRODUCT_LINE_NAME2' ,'SALES_ORDER', sum(EXT_PRICE) as 'sum_EXT_PRICE', sum(EXTCOST) as 'sum_EXTCOST',max('TradingTerms.On_invoice_Discount') as 'TradingTerms.On_invoice_Discount', avg('Freight.sum_gltr_amt') as 'sum_gltr_amt', sum('CONVERTED_QTY_LTR_KG1') as 'sum_CONVERTED_QTY_LTR_KG1';
q=group q by (Country2,Channel2,Customer_Name2,'PRODUCT_LINE_NAME2');
q=foreach q generate Country2,Channel2,Customer_Name2,'PRODUCT_LINE_NAME2',  sum(sum_gltr_amt) as Amount, sum(sum_CONVERTED_QTY_LTR_KG1) as 'sum_Units',sum(sum_EXT_PRICE) as 'sum_EXT_PRICE', sum(sum_EXTCOST) as 'sum_EXTCOST',max('TradingTerms.On_invoice_Discount') as 'max_TradingTerms.On_invoice_Discount',  sum(sum_gltr_amt)/sum(sum_CONVERTED_QTY_LTR_KG1) as 'sum_FreightPerUnit',sum(sum_EXT_PRICE)/sum(sum_CONVERTED_QTY_LTR_KG1) as 'sum_InvoicePricePerUnit', sum(sum_EXT_PRICE)-sum(sum_EXTCOST) as 'sum_Margin',(sum(sum_EXT_PRICE)/sum(sum_CONVERTED_QTY_LTR_KG1))*max('TradingTerms.On_invoice_Discount')/100 as 'sum_TradingTermPerUnit',(sum(sum_EXT_PRICE)/sum(sum_CONVERTED_QTY_LTR_KG1))-((sum(sum_EXT_PRICE)/sum(sum_CONVERTED_QTY_LTR_KG1))*max('TradingTerms.On_invoice_Discount')/100) as 'sum_NetSales',sum(sum_EXTCOST)/sum(sum_CONVERTED_QTY_LTR_KG1) as 'sum_Cogs_Per_Unit';q=foreach q generate Country2,Channel2,Customer_Name2,'PRODUCT_LINE_NAME2', sum(Amount) as Amount,sum(sum_Units) as 'MTD ACTUAL UNITS',round(sum(sum_EXT_PRICE)) as 'MTD Gross Sales($)',sum(sum_EXTCOST) as 'MTD COGS $',max('max_TradingTerms.On_invoice_Discount') as 'CONTRACTED TRADING TERMS (%)',sum(sum_FreightPerUnit) as 'FREIGHT COST PER UNIT',sum(sum_InvoicePricePerUnit) as 'INVOICE PRICE PER UNIT $', sum(sum_Margin) as 'MTD MARGIN $',sum(sum_TradingTermPerUnit) as 'TRADING TERM PER UNIT ($)', sum(sum_NetSales) as 'NET SALES PER UNIT ($)', sum(sum_Cogs_Per_Unit) as 'COGS PER UNIT ($)',sum(sum_NetSales)-sum(sum_Cogs_Per_Unit) as 'GROSS MARGIN PRE FREIGHT PER UNIT ($)',(sum(sum_NetSales)-sum(sum_Cogs_Per_Unit))/sum(sum_NetSales)*100 as 'GM % OF NET SALES',(sum(sum_NetSales)-sum(sum_Cogs_Per_Unit))-sum(sum_FreightPerUnit) as 'GROSS MARGIN AFTER FREIGHT PER UNIT ($)',((sum(sum_NetSales)-sum(sum_Cogs_Per_Unit))-sum(sum_FreightPerUnit))/sum(sum_NetSales)*100 as 'GM AFTER FREIGHT % OF NET SALES';

